var htmlt = '<table width="100%">';

for (var key in msg.payload) {


    htmlt += '<tr><td>' + key.replace(/_/g, ' ') + '</td><td align="right">' + msg.payload[key] + '</td></tr>';
}

htmlt += '<table>';

msg.payload=htmlt;

return msg;

var io = global.get("io");
io = io[msg.block+""+msg.channel];

var msg1={"payload":"1"}
if (!io) {

    return [null,msg1,msg3];
}

msg.io = io;

var msg3 = {};
msg3.payload = msg.channel + ":  " + io["type"];


if(io["type"].indexOf("PULSE")>-1) { msg.payload =  "countrd"; }

else if(io["type"].indexOf("10K")>-1) { 

    if(msg.block=="universal") {   msg.payload =  "r10krd";  }
    else {  msg.payload =  "rthrd";    msg.payload =  "rthrd"; }

}

else if(io["type"].indexOf("10V")>-1) { msg.payload =  "adcrd"; }


else { 

    msg1={"payload":"1"}
    return [null,msg1,msg3];


}

// address & channel
//msg.payload =  msg.board + " " +  msg.payload + " " + msg.channel;
msg.payload =  msg.payload + " " + msg.channel;

return [msg,null,msg3];

var onV = msg.onV || 4;
var offV = 0;

msg.roomtemp = parseFloat(global.get("readings.local." + msg.stat + ".dat.temperature.value") || 30);

if (msg.roomtemp > 21) {

    onV = 0;
    offV = 0;
    msg.delay = 3000;
}

else {


    if (msg.delay > 28000) {

        onV = onV + ((msg.delay - 28000) / 1000)
        offV = 1 + ((msg.delay - 28000) / 1000)
    }

    // Flow temperature adjustment.
    msg.delay = parseInt(msg.delay * (global.get("fAdjust") || 1));
    //if (msg.delay > 5000) { 

    onV = parseInt(onV * 100) / 100;
    if (onV > 9.5) { onV = 9.5; }

    offV = parseInt(offV * 100) / 100;
    if (offV > 8) { offV = 8; }

}

var msg1 = { payload: msg.payload + ("" + onV), topic: msg.topic };
msg.payload = msg.payload + ("" + offV);

//if (msg.delay > 28000) { return [msg1, null]; }

return [msg1, msg];

// zones 1 & 9 may be crossed.


var zones = [
    { cmd: "megabas 2 dacwr 1 ", zone: 1, onV: 2, stat: "sontay113", tRObj: "htg-west.dat.tRZ1"},
    { cmd: "megabas 2 dacwr 2 ", zone: 2, onV: 2, stat: "sontay112", tRObj: "htg-west.dat.tRZ2" },
    { cmd: "megabas 2 dacwr 3 ", zone: 3, onV: 2, stat: "sontay111", tRObj: "htg-west.dat.tRZ3" },
    { cmd: "megabas 0 dacwr 4 ", zone: 4, onV: 2, stat: "sontay110", tRObj: "htg-west.dat.tRZ4", fancmds: ["megabas 0 dacwr 2 ", "megabas 0 dacwr 3 "] },
    { cmd: "megabas 1 dacwr 2 ", zone: 5, onV: 2, stat: "sontay114", tRObj: "htg-west.dat.tRZ5" },
    { cmd: "megabas 2 dacwr 4 ", zone: 6, onV: 2, stat: "sontay115", tRObj: "htg-west.dat.tRZ6" },
    { cmd: "megabas 1 dacwr 4 ", zone: 7, onV: 2, stat: "sontay116", tRObj: "htg-west.dat.tRZ7", fancmds: ["megabas 1 dacwr 3 "] },
    { cmd: "megabas 4 dacwr 2 ", zone: 8, onV: 2, stat: "sontay117", tRObj: "htg-west.dat.tRZ8", fancmds: ["megabas 4 dacwr 1 "] },
    { cmd: "megabas 4 dacwr 4 ", zone: 9, onV: 2, stat: "sontay118", tRObj: "htg-west.dat.tRZ9", fancmds: ["megabas 4 dacwr 3 "] }
]

// 112 / 113 ?


flow.set("zones", zones);

flow.set("deviceid", "htg-west");

return msg;

var timin = 1 * msg.payload;

var cmdsout = "";

var devicePrefix = "htg-west";

var zones = flow.get("zones");
var oat = flow.get("oat") || 10;
if (oat>100) { oat = 0; }

// var readsnow = global.get("readings.local." + devicePrefix + ".dat");
// var reads = flow.get("tR") || {};
// var readslast = flow.get("tRlast") || {};
// var setV = flow.get("setV") || {};

// var tFHistory = global.get("tFHistory") || [];
// var tF = readsnow["tFM"].value || 70;
// tFHistory.push(tF);
// if (tFHistory.length > 20) { tFHistory.shift(); }
// global.set("tFHistory", tFHistory);
// var tFAv = 0;
// for (let h = 0; h < 15; h++) {
//     tFAv = tFAv + (tFHistory[h] || 70);
// }
// tFAv = tFAv / 15;
// var fAdjust = 70 / (Math.max(30, tFAv));
// global.set("fAdjust", fAdjust);

var totd = 0;
var tRPeak = 0;
for (var zone in zones) {
    totd = totd + (zones[zone].gapD || 0);

    if ((timin - (zones[zone].lastOff || timin) > 10000) && (timin - (zones[zone].lastOff || timin) < 15000)) {    // open recent = take return reading and calculate off time.

        zones[zone].tR = global.get("readings.local." + zones[zone].tRObj + ".value") || 30;
    }

    if (zones[zone].tR > tRPeak) { tRPeak = 1.0 * zones[zone].tR; }
}


var avgd = totd / zones.length;
flow.set("avgd", avgd);

for (var zone in zones) {

    var i = zones[zone].zone;

    if (i==1) { continue; }

    zones[zone].nextTime = zones[zone].nextTime || (timin + (i * 2000));
    zones[zone].gapI = zones[zone].gapI || 60000   // 30s starting cycle time in milliseconds.
    zones[zone].gap = zones[zone].gap || 60000   // 30s starting cycle time in milliseconds.

    zones[zone].onTime = zones[zone].onTime || 5000   // 30s starting cycle time in milliseconds.

    zones[zone].onTime = 5000;  // for now


    if (timin < zones[zone].nextTime) { continue; }  // skip until time to act.


    // temporary fix for occasional low readings on 110
    var tRoomT = parseFloat(global.get("readings.local." + zones[zone].stat + ".dat.temperature.value") || 22);
    if (tRoomT < 5) { continue; }
    else { zones[zone].tRoom = tRoomT; }



    zones[zone].tSetRoom = zones[zone].tSetRoom || 22;    // !! NEED to takee from Sontay Dials
    zones[zone].tSetUser = parseFloat(global.get("readings.local." + zones[zone].stat + ".dat.setpoint.value") || 50);


    // pre-heat from 6-9am
    var td = new Date();
    var minuteOfDay = (60 * td.getHours()) + td.getMinutes();
    var tDefault = 21.5;

    zones[zone].tSetRoom = tDefault;

    // if (minuteOfDay <= 360) {

    //     //zones[zone].tSetRoom = 15;
    //     zones[zone].tSetRoom = tDefault;

    // } 
    // else 
    if (minuteOfDay < 480) {

        // ramp up 

        //zones[zone].tSetRoom = (tDefault - 3) + (3 * (minuteOfDay - 480) / 480) ;
        zones[zone].tSetRoom = (tDefault - 8) + (8 * (minuteOfDay) / 480);
        zones[zone].tSetRoom = Math.max(16, zones[zone].tSetRoom);
        zones[zone].tSetRoom = parseInt(100 * zones[zone].tSetRoom) / 100;

        // turn off between 8 and 9 am
        // to allow startup to settle down 

        // if (zones[zone].tRoom < tDefault) {
        //     zones[zone].tSetRoom = (tDefault + zones[zone].tRoom) / 2;
        // }

        // zones[zone].gapI = 300000;
        // zones[zone].onTime = 5000;
        // zones[zone].tSetRoom = 15;

        // } else if (minuteOfDay > 900 && minuteOfDay <= 1020) {

        //     zones[zone].tSetRoom = (tDefault - 1);

    }
    else if (minuteOfDay > 1080) {

        zones[zone].tSetRoom = 16;
        //zones[zone].tSetRoom = tDefault;
    }

    // on constant
    //zones[zone].tSetRoom = tDefault;

    // DONT DO ZONES
   // if (i == 2 || i == 4) { zones[zone].tSetRoom = 16; }


    var co2Room = parseFloat(global.get("readings.local." + zones[zone].stat + ".dat.co2Rise.value") || 0);
    if (co2Room > 40) { zones[zone].lastOccupied = timin; }
    var lastOcc = timin - (zones[zone].lastOccupied || timin);

    if (lastOcc < (1000 * 60 * 60 * 24 * 3)) {

        zones[zone].turndown = 0.0;

    } else if (lastOcc < (1000 * 60 * 60 * 24 * 5)) {

        zones[zone].turndown = 3.0 * (lastOcc - (1000 * 60 * 60 * 24 * 3)) / (1000 * 60 * 60 * 24 * 2);

    } else {

        zones[zone].turndown = 3.0;
    }

    zones[zone].turndown = 0; // no occupancy turn down temporary.

    var setVme;  // set voltage

    if (parseFloat(zones[zone].state || 0) > 0) {

        zones[zone].lastOff = timin;

        //zones[zone].state = 0.5;  // soft close


        if (zones[zone].offV > 0) {

            setVme = zones[zone].offV;
            zones[zone].nextTime = timin + zones[zone].gap;
            zones[zone].state = 0;
        }

        else {

            if (zones[zone].state > 0.2) {

                zones[zone].nextTime = timin + 1000;
                zones[zone].state = zones[zone].state / 2;

                setVme = zones[zone].onV * zones[zone].state;

            }
            else {

                setVme = zones[zone].offV;
                zones[zone].nextTime = timin + zones[zone].gap;
                zones[zone].state = 0;
            }
        }

        //setVme = zones[zone].offV || 0;


        //zones[zone].tR = global.get("readings.local." + zones[zone].tRObj + ".value") || zones[zone].tR || 30;




    }

    else {   // on pulse

        zones[zone].state = 1;

        zones[zone].errT = zones[zone].tRoom + zones[zone].turndown - zones[zone].tSetRoom;

        zones[zone].tRDiff = zones[zone].tR - (zones[zone].tRLast || zones[zone].tR);
        zones[zone].tRLast = zones[zone].tR;



        var integ = 1000;
        zones[zone].gapI = zones[zone].gapI + (zones[zone].errT * integ * (zones[zone].gap / 1000));
        if (zones[zone].gapI > 500000) { zones[zone].gapI = 500000; }  // max off time in milliseconds
        if (zones[zone].gapI < -300000) { zones[zone].gapI = -300000; }  // 

        var prop = 50;
        //if (zones[zone].errT < 0) { prop = prop * 2; } // faster when heating.

        zones[zone].gapP = zones[zone].errT * prop * 10000;
        if (zones[zone].gapP > 500000) { zones[zone].gapP = 300000; }  // max prop
        else if (zones[zone].gapP < -300000) { zones[zone].gapP = -300000; }  // min prop

        var tDM = parseFloat(global.get("readings.local." + zones[zone].stat + ".dat.temperatureDiff.value") || 0);
        var diff = 60;

        // when not in fine control, react more to counter increasing temperatures
        // ... easier to add than remove heat from rooms.
        //if ((zones[zone].errT < -0.3) && (zones[zone].errT > 0.3) && (tDM > 0)) { diff = 60; }
        if (tDM > 0) { diff = 120; }

        //var tRoomLast = 1 * (zones[zone].tRoomLast || 21);
        //zones[zone].tRoomLast = 1 * zones[zone].tRoom;

        var tDiffr = (10000000000 * tDM) / 60000;

        //var tDiffr = (10000000000 * (zones[zone].tRoom - tRoomLast)) / Math.max(1000, (zones[zone].gap || 1000));

        zones[zone].gapD = tDiffr * diff;
        if (zones[zone].gapD > 300000) { zones[zone].gapD = 300000; }  // max diff
        else if (zones[zone].gapD < -300000) { zones[zone].gapD = -300000; }  // max diff

        zones[zone].gap = 0 + (zones[zone].gapI || 0) + (zones[zone].gapP || 0) + (zones[zone].gapD || 0) + (3 * (avgd || 0));

        if (zones[zone].gap < -180000) { zones[zone].gap = -180000; }  // max gap = 4v

        if (zones[zone].errT > 1) { // off but not zero, when room too hot.
            zones[zone].onV = 0.00;
            zones[zone].offV = 0.0;
            zones[zone].gap = 300000;
        }
        else if (zones[zone].gap < 60000) {
            zones[zone].onV = 2 + ((60000 - zones[zone].gap) / 120000)

            // new 21/11/23
            //zones[zone].offV = Math.max(0, zones[zone].onV - 4);

            var offscalar = (zones[zone].onV - 2) * (zones[zone].onV - 2) / 5;
            zones[zone].offV = Math.max(zones[zone].onV / 2, offscalar );

            zones[zone].gap = Math.max(30000, zones[zone].gap);

        } else if (zones[zone].gap > 300000) {
            zones[zone].onV = Math.max(0, 2 - ((zones[zone].gap - 300000) / 150000));
            zones[zone].onV = parseInt(100 * zones[zone].onV) / 100;
            zones[zone].gap = 300000;
            zones[zone].offV = 0;
        }
        else { zones[zone].onV = 2; }


        // Flow Temperature Adjustments
        var tF = flow.get("tF") || 60;
        zones[zone].tFAdjust = 40.0 / (Math.max(45, tF) - 20.0)
        //zones[zone].gap = zones[zone].gap / zones[zone].tFAdjust;
        zones[zone].onTime = Math.max(1500, 5000 * zones[zone].tFAdjust);

        if (zones[zone].onV > 4) {
            //zones[zone].onTime = zones[zone].onTime + 250;
        }



        // if (zones[zone].onV > 5) {
        //     zones[zone].offV = (zones[zone].offV || 0) + 0.025;
        // }
        // if (zones[zone].onV > 2) {  // testing more aggressive offV
        //     zones[zone].offV = zones[zone].onV - 1.5;
        // } else {
        //     zones[zone].offV = 0;
        // }

        // testing with fixed limit of 30C
        // adds both a delay of up to 125s, and voltage reduction, over 5C span (to zero)
        zones[zone].rtnLimitDelay = 0;


        //if (zones[zone].tR > 99) { // temporary.

            if (zones[zone].tR > 30) {

                zones[zone].rtnLimitDelay = Math.max(0, Math.min(125, 5 * (zones[zone].tR - 30)));

                var af = Math.max(10, zones[zone].tR - 30.0) / 10;
                zones[zone].onV = zones[zone].onV * (1 - af);
                zones[zone].offV = zones[zone].offV * (1 - af);
            }

            // if the hottest return, and rising, then good chance valve needs reseating...
            if (zones[zone].tR > 40 && zones[zone].tR == tRPeak && zones[zone].tRDiff > 0) {
                zones[zone].onV = 0.2;
                zones[zone].offV = 0;
                zones[zone].gap = 500000;
                zones[zone].onTime = 2000;
            }
        //}

        if (zones[zone].onTime > 30000) { zones[zone].onTime = 30000; }
        if (zones[zone].offV > 2.5) { zones[zone].offV = 2.5; }


        // minimum movement to help seating. off below 0.2.
        if (zones[zone].onV < 0.2) { zones[zone].onV = 0; }

        if (zones[zone].onV > 7) {
            zones[zone].onV = 7;
        } // max onV

        if (zones[zone].gap < 10000) { zones[zone].gap = 10000; }  // minimum off time in milliseconds
        if (zones[zone].gap > 500000) { zones[zone].gap = 500000; }  // max off time in milliseconds


        if (zones[zone].onV < ((zones[zone].offV || 0) + 0.2)) {
            zones[zone].offV = Math.max(0, zones[zone].offV - 0.01);  // wind down slow..
        }

        setVme = parseFloat(zones[zone].onV);
        zones[zone].tR = zones[zone].tR || 30;

        zones[zone].lastOn = timin;
        zones[zone].nextTime = timin + zones[zone].onTime;

    }

    // temporary
    //if (zones[zone].zone == 9) { setVme = 0; } 
    //else { setVme = 0; }


    setVme = parseInt(setVme * 100) / 100;

    //setVme = 0;
    if (setVme < 0) { setVme = 0; }


    zones[zone].setV = setVme;
    cmdsout += (cmdsout == "" ? "" : " && ") + zones[zone].cmd + zones[zone].setV;

    if (zones[zone].fancmds && zones[zone].state == 1) {
        for (var fan in zones[zone].fancmds) {
            // changed fan speed 21/11/23
            var fv = 19 - oat; //zones[zone].setV - zones[zone].errT;
            if (fv < 0.5) { fv = 0.5; }
            if (fv > 8) { fv = 8; }
            fv = parseInt(fv * 100) / 100;
            cmdsout += (cmdsout == "" ? "" : " && ") + zones[zone].fancmds[fan] + fv;
        }
    }

    var msg2 = {};
    msg2.topic = devicePrefix + "-manifold/dat/setV" + i;
    // htg-east-manifold/dat/setV9
    msg2.payload = setVme;

    node.send([null, msg2]);

    // }





}

if (cmdsout !== "") {

    var msg1 = {};
    msg1.topic = "cmdsout"
    msg1.payload = cmdsout;

    node.send([msg1, null]);

}

